import React, { Component } from 'react'

export default class MakeTransaction extends Component {

    constructor(props) {
        super(props);

        this.state = {
            from: ' ',
            to: ' ',
            amount: 0,
            users: []
        }
    }

    onChangeFrom(e){
        this.setState({
            from: e.target.value
        });
    }

    onChangeTo(e){
        this.setState({
            to: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
    }

    onChangeAmount(e){
        this.setState({
            amount: e.target.value
        });
    }

    render(){
        return (
            <div>MakeTransaction</div>
          )
    }
}
