import React, { Component } from 'react'
import styled from 'styled-components';

const Section = styled.section`
position: relative;
min-height: 40vh;
width: 80vw;
display: flex;
flex-direction: column;
margin: 0 auto;
justify-content: space-evenly;
`

const Title = styled.h1`
font-size: ${props => props.theme.fontxxxl};
font-family: "Audiowide";
font-weight: 300;
position: absolute;
top: 1rem;
left: 5%;
z-index: 5;
`
const Form = styled.form`
font-family: "Montserrat";
font-weight: 400;
display: flex;
flex-direction: column;
font-size: ${props => props.theme.fontmd};
padding: 100px;
margin: auto;
`

const FormGroup = styled.div`
margin-top: 1em;
display: flex;
flex-direction: column;

select, input{
    height: 2em;
    font-family: "Montserrat";
    width: 60vw;
}

.submitButton{
    border-radius: 50px;
    width: 10em;
    height: 3em;
    margin: auto;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: bold;
    background-color: lightyellow;

    &:hover{
        transition: all 0.3s ease;
        transform: scale(1.1);
        background-color: yellow;
    }
}

/* CSS */
.button-52 {
  font-size: 16px;
  font-weight: 200;
  letter-spacing: 1px;
  padding: 13px 20px 13px;
  outline: 0;
  border: 1px solid black;
  cursor: pointer;
  position: relative;
  background-color: rgba(255, 0, 0, 0);
  user-select: none;
  -webkit-user-select: none;
  touch-action: manipulation;
  color: wheat;


&:after {
  content: "";
  background-color: #ffe54c;
  width: 100%;
  position: absolute;
  height: 100%;
  top: 7px;
  left: 7px;
  transition: 0.2s;
}

&:hover:after {
  top: 0px;
  left: 0px;
}
}
`



export default class MakeTransaction extends Component {

    constructor(props) {
        super(props);

        this.textInput = null;

        this.setTextInputRef = element => {
            this.textInput = element;
          };

          this.focusTextInput = () => {
            // Focus the text input using the raw DOM API
            if (this.textInput) this.textInput.focus();
          };

        this.onChangeFrom = this.onChangeFrom.bind(this);
        this.onChangeTo = this.onChangeTo.bind(this);
        this.onChangeAmount = this.onChangeAmount.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            from: ' ',
            to: ' ',
            amount: 0,
            users: []
        }
    }

    componentDidMount(){
        this.setState({
            users: ['test user'],
            from: 'test user'
        })
        this.focusTextInput();
    }

    onChangeFrom(e){
        this.setState({
            from: e.target.value
        });
    }

    onChangeTo(e){
        this.setState({
            to: e.target.value
        });
    }

    onChangeAmount(e){
        this.setState({
            amount: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const transaction = {
            from: this.state.from,
            to: this.state.to,
            amount: this.state.amount
        }

        console.log(transaction);

        window.location = '/transactions';
    }


    render(){
        return (
            <Section>
                <Title data-scroll data-scroll-speed="-2" data-scroll-direction="horizontal">
                Make Transaction
                </Title>
                <Form onSubmit={this.onSubmit}>
                    <FormGroup>
                        <label>From: </label>
                        <select ref={this.setTextInputRef}
                                    required
                                    className='formControl'
                                    value={this.state.from}
                                    onChange={this.onChangeFrom}>
                                    {
                                        this.state.users.map(function(user){
                                            return <option
                                                    key={user}
                                                    value={user}>{user}
                                                    </option>
                                        })
                                    }

                        </select>
                    </FormGroup>

                    <FormGroup>
                        <label>To: </label>
                        <select ref={this.setTextInputRef}
                                    required
                                    className='formControl'
                                    value={this.state.to}
                                    onChange={this.onChangeTo}>
                                    {
                                        this.state.users.map(function(user){
                                            return <option
                                                    key={user}
                                                    value={user}>{user}
                                                    </option>
                                        })
                                    }

                        </select>
                    </FormGroup>

                    <FormGroup>
                        <label>Amount: </label>
                        <input 
                        type="text"
                        required
                        className='formControl'
                        value={this.state.amount}
                        onChange={this.onChangeAmount}
                        />
                    </FormGroup>

                    <FormGroup>
                        <input type="submit" value="Make Transaction" onClick={this.focusTextInput} className='button-52'/> 
                    </FormGroup>
                </Form>
            </Section>
          )
    }
}
